function abreOuFechaResposta(e){e.target.parentNode.classList.toggle("faq__questions__item--is-open")}function removeBotaoAtivo(){var t=document.querySelectorAll("[data-tab-button]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__tabs__button--is-active")}function esconde(){var t=document.querySelectorAll("[data-tab-id]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__list--is-active")}document.addEventListener("DOMContentLoaded",function(){var t=document.querySelectorAll("[data-tab-button]");for(let e=0;e<t.length;e++)t[e].addEventListener("click",function(e){var t=e.target.dataset.tabButton,t=document.querySelector(`[data-tab-id=${t}]`);esconde(),t.classList.add("shows__list--is-active"),removeBotaoAtivo(),e.target.classList.add("shows__tabs__button--is-active")});let e=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){window.scrollY<e?document.querySelector("header").classList.add("header--is-hidden"):document.querySelector("header").classList.remove("header--is-hidden")});var o=document.querySelectorAll("[data-faq-questions]");for(let e=0;e<o.length;e++)o[e].addEventListener("click",abreOuFechaResposta)});